import unittest

class TestAdventOfCode_Day01(unittest.TestCase):
    def testCountFloors_WithMatchedNested_ReturnsZero(self):
        actual = countFloors("(())")
        self.assertEqual(actual, 0)

    def testCountFloors_WithMatchedNotNested_ReturnsZero(self):
        actual = countFloors("()()")
        self.assertEqual(actual, 0)

    def testCountFloors_WithThreeLeftParens_ReturnsThree(self):
        actual = countFloors("(((")
        self.assertEqual(actual, 3)

    def testCountFloors_WithFiveLeftParensAndTwoRight_ReturnsThree(self):
        actual = countFloors("(()(()(")
        self.assertEqual(actual, 3)

    def testCountFloors_WithThreeRightParens_ReturnsNegativeThree(self):
        actual = countFloors(")))")
        self.assertEqual(actual, -3)

    def testCountFloors_WithFiveRightParensAndTwoLeft_ReturnsNegativeThree(self):
        actual = countFloors(")())())")
        self.assertEqual(actual, -3)

    def testCountFloors_WithNonParenCharacters_IgnoresNonParenCharacters(self):
        actual = countFloors("(_(a(42)")
        self.assertEqual(actual, 2)

    def testFindFloor_WithEmptyString_ReturnsNone(self):
        actual = findFirstBasementFloor("")
        self.assertEqual(actual, None)

    def testFindFloor_WithNone_ReturnsNone(self):
        actual = findFirstBasementFloor(None)
        self.assertEqual(actual, None)

    def testFindFloor_AtPositionOne_ReturnsOne(self):
        actual = findFirstBasementFloor(")")
        self.assertEqual(actual, 1)

    def testFindFloor_AtPositionFive_ReturnsFive(self):
        actual = findFirstBasementFloor("()())")
        self.assertEqual(actual, 5)

def countFloors(floorMap):
    result = 0
    result += floorMap.count('(')
    result -= floorMap.count(')')
    return result


def findFloor(floorMap, floorToFind):
    if (floorMap == None or floorMap == ""):
        return None
    currentFloor = 0
    currentPosition = 0
    for floor in floorMap:
        currentPosition += 1
        if floor == '(':
            currentFloor += 1
        elif floor == ')':
            currentFloor -= 1
        if currentFloor == floorToFind:
            return currentPosition

def findFirstBasementFloor(floorMap):
    return findFloor(floorMap, -1)


